            #             for {key}'
            #             self.log_ver.add_msg(log_msg=re.escape(a_msg))
            #             logger.debug(a_msg)

            while self.get_log_msgs():
                while self.log_found_items:
                    found_log_item = self.log_found_items.popleft()

                    # log the log msg being processed but mangle it a
